sp_widget.client_script:
function($scope, $rootScope) {
	/* widget controller */
	var c = this;
	$rootScope.$on('selectNote',function(event,data){
		console.log("trying to call server with noteID:"+$rootScope.selectedNote);
		c.server.get({
			action:"getNote",
			noteID:$rootScope.selectedNote
		}).then(function(resp){
			c.data.title = resp.data.note.title;
			c.data.note	=	resp.data.note.note;
			c.data.noteID = resp.data.note.sys_id;
		})

		console.log("in Notes Body widget. selectNote event received. data: "+JSON.stringify(c.data));
		console.log(event);
	});

	c.updateNote = function(updateType) {
		var editedNote={
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}
		c.server.get(editedNote).then(function(r) {
			$rootScope.editedNote = editedNote;
			$rootScope.$emit('noteEdited',c.data.noteID);
		});
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input && input.noteID){
		gs.info("Notes application server call with input:"+input.noteID);
		var noteGR = new GlideRecord("x_snc_createnotes_note");
		if(noteGR.get(input.noteID)){
			if(input.action=="getNote"){
				gs.info("Notes application sp.getRecordvalues will attempt" );
				data.note= {};
				$sp.getRecordValues(data.note, noteGR,"title, note, sys_id");
				console.log("Notes application retrieved record values = "+JSON.stringify(data))
			}else if(input.action=="updateNote"){
				noteGR.title=input.noteTitle;
				noteGR.note=input.noteBody;
				noteGR.update();
			}

		}

	}

})();
